%pyspark
def getkeys(mydict):
    return list(mydict.keys())

def create_ones(key):
    return key, 1

def reduction(prev, value):
    return prev + value

# take a look at the distinct keys
distinct_keys = dataset.flatMap(getkeys).distinct()
# turn keys into (key, 1) tuples and sum the ones
counts_per_key = dataset.flatMap(getkeys).map(create_ones).reduceByKey(reduction)

