import pprint

def estimate_publish_date(datapoint):

	# its a string so default to a Y 0000 string
	publish_date = parse_publish_date(datapoint.get('publish_date', '0001'))

	return (
		datapoint['key'], # always present
		datapoint.get('full_title', '__UNKNOWN__'), # not always present, use __UNKNOWN__ placeholder
		publish_date
	)

def parse_publish_date(pubdate):

	try:
	    parsed = datetime.datetime(year=int(pubdate[-4:]), month=1, day=1)
	except:
	    parsed = datetime.datetime(year=1, month=1, day=1)

	return parsed

publication_data = dataset.map(estimate_publish_date)
